//SPDX-License-Identifier:MIT
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";

contract TokenFarm is Ownable {
    address[] public tokensAllowed;
    address[] public stakers;
    // mapping token address -> staker address -> amount
    mapping(address => mapping(address => uint256)) public stakingBalance;
    mapping(address => uint256) public uniqueTokensStaked;
    mapping(address => address) public tokenPriceFeedMapping;
    IERC20 public dappToken;

    constructor(address dapptokenAddress) {
        dappToken = IERC20(dapptokenAddress);
    }

    // stake, unstake, issue, addAllowed, getValue of tokens
    function stakeTokens(uint256 _amount, address _token) public {
        // what tokens can user stake?
        // how much can users stake?
        require(_amount > 0, "Amount must be more than 0.");
        // check if token is allowed?
        require(tokenIsAllowed(_token), "Token is not Allowed.");
        //transferFrom -> require ABI
        IERC20(_token).transferFrom(msg.sender, address(this), _amount);
        updateUniqueTokensStaked(msg.sender, _token);
        stakingBalance[_token][msg.sender] =
            stakingBalance[_token][msg.sender] +
            _amount;
        if (uniqueTokensStaked[msg.sender] == 1) {
            stakers.push(msg.sender);
        }
    }

    function unstakeTokens(address token) public {
        uint256 balance = stakingBalance[token][msg.sender];
        require(balance > 0, "You have no tokens to stake");
        IERC20(token).transfer(msg.sender, balance);
        stakingBalance[token][msg.sender] = 0;
        uniqueTokensStaked[msg.sender] = uniqueTokensStaked[msg.sender] - 1;
        // delete person from stakers array.
    }

    // will solve the problem of issuing tokens to users, who have multiple tokens.
    function issueTokens() public onlyOwner {
        for (
            uint256 stakersIndex = 0;
            stakersIndex < stakers.length;
            stakersIndex++
        ) {
            address recipient = stakers[stakersIndex];
            uint256 userTotalValue = getUserTotalValue(recipient);
            // send them a token reward based on their total value locked.
            dappToken.transfer(recipient, userTotalValue);
        }
    }

    function getUserTotalValue(address _user) public view returns (uint256) {
        uint256 totalValue = 0;
        require(
            uniqueTokensStaked[_user] > 0,
            "Token to stake karle pehele! Reward lene chala hai saaalaaa..."
        );
        for (
            uint256 allowedTokenIndex = 0;
            allowedTokenIndex < tokensAllowed.length;
            allowedTokenIndex++
        ) {
            totalValue =
                totalValue +
                getUserSingleTokenValue(
                    _user,
                    tokensAllowed[allowedTokenIndex]
                );
        }
        return totalValue;
    }

    function getUserSingleTokenValue(address user, address token)
        public
        view
        returns (uint256)
    {
        // we want the price in dollars.
        if (uniqueTokensStaked[user] <= 0) {
            return 0;
        }
        // price of the token * stakingBalance[token][user]
        (uint256 price, uint256 decimals) = getPriceofToken(token);
        return ((price * stakingBalance[token][user]) / (10**decimals));
    }

    function getPriceofToken(address token)
        public
        view
        returns (uint256, uint256)
    {
        // priceFeedAddress
        address priceFeedAddress = tokenPriceFeedMapping[token];
        AggregatorV3Interface priceFeed = AggregatorV3Interface(
            priceFeedAddress
        );
        (, int256 price, , , ) = priceFeed.latestRoundData();
        uint256 decimals = uint256(priceFeed.decimals());
        return (uint256(price), decimals);
    }

    function getPriceFeedContract(address _token, address _priceFeed)
        public
        onlyOwner
    {
        tokenPriceFeedMapping[_token] = _priceFeed;
    }

    function updateUniqueTokensStaked(address _user, address _token) internal {
        if (stakingBalance[_token][_user] <= 0) {
            uniqueTokensStaked[_user] = uniqueTokensStaked[_user] + 1;
        }
    }

    function addAllowedTokens(address _token) public onlyOwner {
        tokensAllowed.push(_token);
    }

    function tokenIsAllowed(address _token) public returns (bool) {
        for (
            uint256 allowedTokensIndex = 0;
            allowedTokensIndex < tokensAllowed.length;
            allowedTokensIndex++
        ) {
            if (tokensAllowed[allowedTokensIndex] == _token) {
                return true;
            }
        }
        return false;
    }
}
